import { styled } from "styled-components";

export const Container = styled.div`
  position: relative;
  width: 393px;
  height: 816px;
  align-items: center;
  background: #fff;
  margin: 0 auto;
`;

export const Bar = styled.div`
  display: flex;
  flex-direction: row;
  width: 393px;
  height: 44px;
  flex-shrink: 0;
  #Time {
    margin-top: 12px;
    margin-left: 10px;
    float: right;
    position: absolute;
    color: #000;
    text-align: center;
    font-family: "Pretendard Variable";
    font-size: 18px;
    font-style: normal;
    font-weight: 500;
    line-height: 25px; /* 138.889% */
  }
  #Connection {
    margin-top: 14px;
    margin-left: 310px;
    width: 16px;
    height: 12.173px;
    flex-shrink: 0;
  }
  #Wifi {
    margin-top: 14px;
    margin-left: 8px;
    width: 16px;
    height: 12.173px;
    flex-shrink: 0;
  }
  #Battery {
    margin-top: 14px;
    margin-left: 8px;
    width: 25px;
    height: 12.173px;
    flex-shrink: 0;
  }
`;

export const X = styled.div`
  width: 20px;
  height: 20px;
  margin-top: 20px;
  margin-left: 20px;
`;

export const Success = styled.div`
  width: 293px;
  height: 116px;

  #brand {
    color: #530093;
    font-family: "Pretendard Variable";
    font-size: 30px;
    font-style: normal;
    font-weight: 500;
    line-height: normal;
  }

  #detail {
    color: #000;
    font-family: "Pretendard Variable";
    font-size: 30px;
    font-style: normal;
    font-weight: 500;
    line-height: normal;
  }
`;
